// src/components/Footer.js
import React from 'react';
import { useLocation, useNavigate } from 'react-router-dom';

function Footer() {
  const location = useLocation();
  const navigate = useNavigate();

  const scrollToSection = (id) => {
    const el = document.getElementById(id);
    if (el) {
      el.scrollIntoView({ behavior: 'smooth' });
    }
  };

  const handleScrollLink = (id) => {
    if (location.pathname !== '/') {
      navigate('/');
      setTimeout(() => scrollToSection(id), 100);
    } else {
      scrollToSection(id);
    }
  };

  return (
    <footer className="footer">
      <div className="footer-links">
        <button onClick={() => handleScrollLink('features')} className="footer-link">
          Features
        </button>
        <button onClick={() => handleScrollLink('audience')} className="footer-link">
          For Who
        </button>
      </div>
      <div className="footer-copy">
        <p>&copy; {new Date().getFullYear()} LocalMart. All rights reserved.</p>
      </div>
    </footer>
  );
}

export default Footer;
