import React, { useState } from 'react';
import { ShoppingCart, Clock, MapPin, CreditCard, FileText } from 'lucide-react';
import { orderService } from '../../../services/orderService';

const OrderConfirmation = ({ 
  cartItems, 
  shopData, 
  onOrderSuccess, 
  onCancel 
}) => {
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  
  // Form state
  const [deliveryAddress, setDeliveryAddress] = useState({
    hostelName: '',
    roomNumber: '',
    landmark: '',
    phoneNumber: ''
  });
  
  const [deliveryTime, setDeliveryTime] = useState('');
  const [paymentMethod, setPaymentMethod] = useState('cash_on_delivery');
  const [specialInstructions, setSpecialInstructions] = useState('');

  // Calculate totals
  const subtotal = cartItems.reduce((sum, item) => sum + (item.price * item.quantity), 0);
  const deliveryFee = subtotal > 200 ? 0 : 20;
  const totalAmount = subtotal + deliveryFee;

  const handleConfirmOrder = async () => {
    try {
      setLoading(true);
      setError('');

      // Validate form
      if (!deliveryAddress.hostelName || !deliveryAddress.roomNumber || 
          !deliveryAddress.phoneNumber || !deliveryTime) {
        setError('Please fill in all required fields');
        return;
      }

      // Prepare order data
      const orderData = {
        shopId: shopData._id,
        items: cartItems.map(item => ({
          productId: item.productId || item._id,
          quantity: item.quantity,
          specialInstructions: item.specialInstructions || ''
        })),
        deliveryAddress,
        deliveryTime,
        paymentMethod,
        specialInstructions
      };

      // Create order
      const result = await orderService.createOrder(orderData);
      
      if (result.success) {
        onOrderSuccess(result.data);
      } else {
        setError(result.message || 'Failed to create order');
      }

    } catch (err) {
      setError(err.message || 'Something went wrong. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="order-confirmation-modal">
      <div className="modal-content">
        <h2>Confirm Your Order</h2>
        
        {error && (
          <div className="error-message">
            {error}
          </div>
        )}

        {/* Order Summary */}
        <div className="order-summary">
          <h3>Order Summary</h3>
          <div className="shop-info">
            <strong>{shopData.name}</strong>
            <span className="category">{shopData.category}</span>
          </div>
          
          <div className="items-list">
            {cartItems.map((item, index) => (
              <div key={index} className="order-item">
                <span className="item-name">{item.name}</span>
                <span className="item-details">
                  {item.quantity} × ₹{item.price} = ₹{item.quantity * item.price}
                </span>
              </div>
            ))}
          </div>
          
          <div className="price-breakdown">
            <div className="price-row">
              <span>Subtotal:</span>
              <span>₹{subtotal}</span>
            </div>
            <div className="price-row">
              <span>Delivery Fee:</span>
              <span>₹{deliveryFee}</span>
            </div>
            <div className="price-row total">
              <strong>
                <span>Total:</span>
                <span>₹{totalAmount}</span>
              </strong>
            </div>
          </div>
        </div>

        {/* Delivery Details Form */}
        <div className="delivery-details">
          <h3><MapPin size={20} /> Delivery Details</h3>
          
          <div className="form-group">
            <label>Hostel Name *</label>
            <input
              type="text"
              value={deliveryAddress.hostelName}
              onChange={(e) => setDeliveryAddress(prev => ({
                ...prev,
                hostelName: e.target.value
              }))}
              placeholder="e.g., Hostel A"
              required
            />
          </div>
          
          <div className="form-row">
            <div className="form-group">
              <label>Room Number *</label>
              <input
                type="text"
                value={deliveryAddress.roomNumber}
                onChange={(e) => setDeliveryAddress(prev => ({
                  ...prev,
                  roomNumber: e.target.value
                }))}
                placeholder="e.g., 201"
                required
              />
            </div>
            <div className="form-group">
              <label>Phone Number *</label>
              <input
                type="tel"
                value={deliveryAddress.phoneNumber}
                onChange={(e) => setDeliveryAddress(prev => ({
                  ...prev,
                  phoneNumber: e.target.value
                }))}
                placeholder="Your contact number"
                required
              />
            </div>
          </div>
          
          <div className="form-group">
            <label>Landmark (Optional)</label>
            <input
              type="text"
              value={deliveryAddress.landmark}
              onChange={(e) => setDeliveryAddress(prev => ({
                ...prev,
                landmark: e.target.value
              }))}
              placeholder="Near library, Ground floor, etc."
            />
          </div>
        </div>

        {/* Delivery Time */}
        <div className="delivery-time">
          <h3><Clock size={20} /> Preferred Delivery Time</h3>
          <input
            type="time"
            value={deliveryTime}
            onChange={(e) => setDeliveryTime(e.target.value)}
            required
          />
        </div>

        {/* Payment Method */}
        <div className="payment-method">
          <h3><CreditCard size={20} /> Payment Method</h3>
          <div className="payment-options">
            <label>
              <input
                type="radio"
                value="cash_on_delivery"
                checked={paymentMethod === 'cash_on_delivery'}
                onChange={(e) => setPaymentMethod(e.target.value)}
              />
              Cash on Delivery
            </label>
            <label>
              <input
                type="radio"
                value="upi"
                checked={paymentMethod === 'upi'}
                onChange={(e) => setPaymentMethod(e.target.value)}
              />
              UPI Payment
            </label>
          </div>
        </div>

        {/* Special Instructions */}
        <div className="special-instructions">
          <h3><FileText size={20} /> Special Instructions (Optional)</h3>
          <textarea
            value={specialInstructions}
            onChange={(e) => setSpecialInstructions(e.target.value)}
            placeholder="Any special requests or instructions for your order..."
            rows={3}
          />
        </div>

        {/* Action Buttons */}
        <div className="action-buttons">
          <button 
            onClick={onCancel}
            className="cancel-btn"
            disabled={loading}
          >
            Cancel
          </button>
          <button 
            onClick={handleConfirmOrder}
            className="confirm-btn"
            disabled={loading}
          >
            {loading ? (
              <>
                <div className="spinner" />
                Creating Order...
              </>
            ) : (
              <>
                <ShoppingCart size={16} />
                Confirm Order - ₹{totalAmount}
              </>
            )}
          </button>
        </div>
      </div>
    </div>
  );
};

export default OrderConfirmation;
