import React, { useState, useEffect } from 'react';
import { Heart, Search, Filter, Star, MapPin, ShoppingCart, Plus, Minus } from 'lucide-react';
import '@fortawesome/fontawesome-free/css/all.min.css';
import '../buyer.css';

// Shop Card Component
function ShopCard({ shop, onVisitShop }) {
    const [isFavorite, setIsFavorite] = useState(false);
    
    const handleFavoriteClick = (e) => {
        e.stopPropagation();
        setIsFavorite(!isFavorite);
    };
    
    const getMainCategory = () => {
        if (!shop.menuItems || shop.menuItems.length === 0) return 'No items';
        
        const categoryCount = shop.menuItems.reduce((acc, item) => {
            acc[item.category] = (acc[item.category] || 0) + 1;
            return acc;
        }, {});
        
        const mainCategory = Object.keys(categoryCount).reduce((a, b) => 
            categoryCount[a] > categoryCount[b] ? a : b
        );
        
        return mainCategory.charAt(0).toUpperCase() + mainCategory.slice(1);
    };
    
    return (
        <div className="buyer-shop-card">
            <div className="buyer-shop-image-container">
                <img 
                    src={shop.profileImage || '/api/placeholder/200/150'} 
                    alt={shop.name} 
                    className="buyer-shop-image" 
                />
                <div className="buyer-shop-tags">
                    <span className="buyer-shop-tag">{shop.totalItems} items</span>
                    <span className="buyer-shop-tag">Popular</span>
                </div>
                <div 
                    onClick={handleFavoriteClick}
                    className="buyer-shop-favorite"
                >
                    <Heart 
                        className="buyer-favorite-icon" 
                        fill={isFavorite ? "#f44336" : "none"} 
                        color={isFavorite ? "#f44336" : "white"}
                        size={20}
                    />
                </div>
            </div>
            
            <div className="buyer-shop-content">
                <div className="buyer-shop-header">
                    <h3 className="buyer-shop-name">{shop.name}</h3>
                    <span className="buyer-shop-category">{getMainCategory()}</span>
                </div>
                
                <div className="buyer-shop-rating">
                    <Star className="buyer-star-icon" fill="#fbbf24" color="#fbbf24" size={16} />
                    <span className="buyer-rating-value">{shop.rating}</span>
                    <span className="buyer-rating-reviews">({shop.reviews} reviews)</span>
                </div>
                
                <p className="buyer-shop-description">
                    {shop.businessAddress || 'Local shop offering quality products'}
                </p>
                
                <div className="buyer-shop-meta">
                    <div className="buyer-shop-location">
                        <MapPin size={14} className="buyer-location-icon" />
                        <span>{shop.businessAddress || 'Campus Location'}</span>
                    </div>
                    <div className="shop-delivery">
                        <i className="fa-solid fa-clock buyer-delivery-icon"></i>
                        <span>{shop.deliveryTime}</span>
                    </div>
                </div>
                
                <button 
                    className="buyer-shop-visit-button"
                    onClick={() => onVisitShop(shop)}
                >
                    Visit Shop
                </button>
            </div>
        </div>
    );
}

// Menu Modal Component
function MenuModal({ shop, isOpen, onClose, onAddToCart }) {
    const [selectedItems, setSelectedItems] = useState({});
    const [cart, setCart] = useState([]);
    
    if (!isOpen || !shop) return null;
    
    const handleQuantityChange = (itemId, change) => {
        setSelectedItems(prev => {
            const currentQty = prev[itemId] || 0;
            const newQty = Math.max(0, currentQty + change);
            
            if (newQty === 0) {
                const { [itemId]: removed, ...rest } = prev;
                return rest;
            }
            
            return { ...prev, [itemId]: newQty };
        });
    };
    
    const getTotalItems = () => {
        return Object.values(selectedItems).reduce((sum, qty) => sum + qty, 0);
    };
    
    const getTotalPrice = () => {
        return Object.entries(selectedItems).reduce((total, [itemId, qty]) => {
            const item = shop.menuItems.find(item => item._id === itemId);
            return total + (item ? item.price * qty : 0);
        }, 0);
    };
    
    const handleAddToCart = () => {
        const cartItems = Object.entries(selectedItems).map(([itemId, quantity]) => {
            const item = shop.menuItems.find(item => item._id === itemId);
            return {
                menuItemId: itemId,
                itemName: item.itemName,
                price: item.price,
                quantity: quantity,
                subtotal: item.price * quantity
            };
        });
        
        onAddToCart(shop.id, cartItems);
        setSelectedItems({});
        onClose();
    };
    
    const groupedMenuItems = shop.menuItems.reduce((acc, item) => {
        const category = item.category.charAt(0).toUpperCase() + item.category.slice(1);
        if (!acc[category]) acc[category] = [];
        acc[category].push(item);
        return acc;
    }, {});
    
    return (
        <div className="menu-modal-overlay" onClick={onClose}>
            <div className="menu-modal" onClick={e => e.stopPropagation()}>
                <div className="menu-modal-header">
                    <div>
                        <h2>{shop.name}</h2>
                        <p>{shop.businessAddress}</p>
                    </div>
                    <button className="close-button" onClick={onClose}>&times;</button>
                </div>
                
                <div className="menu-modal-content">
                    {Object.keys(groupedMenuItems).length === 0 ? (
                        <div className="no-menu-items">
                            <p>No menu items available</p>
                        </div>
                    ) : (
                        Object.entries(groupedMenuItems).map(([category, items]) => (
                            <div key={category} className="menu-category">
                                <h3 className="category-title">{category}</h3>
                                <div className="menu-items-grid">
                                    {items.map(item => (
                                        <div key={item._id} className="menu-item-card">
                                            <div className="menu-item-info">
                                                <h4>{item.itemName}</h4>
                                                <p className="menu-item-description">
                                                    {item.description || 'Delicious item from our kitchen'}
                                                </p>
                                                <span className="menu-item-price">${item.price.toFixed(2)}</span>
                                            </div>
                                            <div className="menu-item-controls">
                                                {selectedItems[item._id] ? (
                                                    <div className="quantity-controls">
                                                        <button 
                                                            onClick={() => handleQuantityChange(item._id, -1)}
                                                            className="quantity-btn"
                                                        >
                                                            <Minus size={16} />
                                                        </button>
                                                        <span className="quantity">{selectedItems[item._id]}</span>
                                                        <button 
                                                            onClick={() => handleQuantityChange(item._id, 1)}
                                                            className="quantity-btn"
                                                        >
                                                            <Plus size={16} />
                                                        </button>
                                                    </div>
                                                ) : (
                                                    <button 
                                                        onClick={() => handleQuantityChange(item._id, 1)}
                                                        className="add-item-btn"
                                                    >
                                                        <Plus size={16} />
                                                        Add
                                                    </button>
                                                )}
                                            </div>
                                        </div>
                                    ))}
                                </div>
                            </div>
                        ))
                    )}
                </div>
                
                {getTotalItems() > 0 && (
                    <div className="menu-modal-footer">
                        <div className="cart-summary">
                            <span>{getTotalItems()} items â€¢ ${getTotalPrice().toFixed(2)}</span>
                        </div>
                        <button 
                            className="add-to-cart-btn"
                            onClick={handleAddToCart}
                        >
                            <ShoppingCart size={18} />
                            Add to Cart
                        </button>
                    </div>
                )}
            </div>
        </div>
    );
}

// Filter Search Component
function FilterSearch({ searchTerm, setSearchTerm, selectedCategory, setSelectedCategory, categories }) {
    return (
        <div className="buyer-filter-search-container">
            <div className="buyer-search-container">
                <div className="buyer-search-input-wrapper">
                    <Search className="buyer-search-icon" size={20} />
                    <input
                        type="text"
                        placeholder="Search shops..."
                        value={searchTerm}
                        onChange={(e) => setSearchTerm(e.target.value)}
                        className="buyer-search-input"
                    />
                </div>
                <button className="buyer-filter-button">
                    <Filter size={18} />
                    <span>Filter</span>
                </button>
            </div>
            
            <div className="buyer-category-filters">
                {categories.map((category, index) => (
                    <button
                        key={index}
                        className={`category-filter ${selectedCategory === category.name ? 'active' : ''}`}
                        onClick={() => setSelectedCategory(category.name)}
                    >
                        {category.name} ({category.count})
                    </button>
                ))}
            </div>
        </div>
    );
}

// Main Component
function ExploreShopsContent() {
    const [shops, setShops] = useState([]);
    const [categories, setCategories] = useState([{ name: 'All', count: 0 }]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [searchTerm, setSearchTerm] = useState('');
    const [selectedCategory, setSelectedCategory] = useState('All');
    const [selectedShop, setSelectedShop] = useState(null);
    const [showMenuModal, setShowMenuModal] = useState(false);
    
    // Fetch shops data
    useEffect(() => {
        fetchShops();
        fetchCategories();
    }, [selectedCategory, searchTerm]);
    
    const fetchShops = async () => {
        try {
            setLoading(true);
            const queryParams = new URLSearchParams();
            if (selectedCategory !== 'All') queryParams.append('category', selectedCategory);
            if (searchTerm) queryParams.append('search', searchTerm);
            
            console.log(`/api/shops?${queryParams}`);
            const response = await fetch(`/api/shops?${queryParams}`);
            const data = await response.json();
            
            if (data.success) {
                setShops(data.data);
            } else {
                setError(data.message);
            }
        } catch (err) {
            setError('Failed to fetch shops');
            console.error('Error fetching shops:', err);
        } finally {
            setLoading(false);
        }
    };
    
    const fetchCategories = async () => {
        try {
            const response = await fetch('/api/shops/categories/stats');
            const data = await response.json();
            
            if (data.success) {
                setCategories(data.data);
            }
        } catch (err) {
            console.error('Error fetching categories:', err);
        }
    };
    
    const handleVisitShop = (shop) => {
        setSelectedShop(shop);
        setShowMenuModal(true);
    };
    
    const handleAddToCart = (shopId, cartItems) => {
        // Here you would typically add items to a global cart state or context
        console.log('Adding to cart:', { shopId, cartItems });
        
        // For now, just show an alert
        const totalItems = cartItems.reduce((sum, item) => sum + item.quantity, 0);
        const totalPrice = cartItems.reduce((sum, item) => sum + item.subtotal, 0);
        
        alert(`Added ${totalItems} items to cart!\nTotal: $${totalPrice.toFixed(2)}\n\nNote: Implement cart functionality in your app.`);
    };
    
    if (loading) {
        return (
            <div className="buyer-explore-content">
                <div className="loading-container">
                    <div className="spinner"></div>
                    <p>Loading shops...</p>
                </div>
            </div>
        );
    }
    
    if (error) {
        return (
            <div className="buyer-explore-content">
                <div className="error-container">
                    <h3>Error loading shops</h3>
                    <p>{error}</p>
                    <button onClick={fetchShops} className="retry-button">
                        Try Again
                    </button>
                </div>
            </div>
        );
    }
    
    return (
        <div className="buyer-explore-content">
            <div className="buyer-explore-header">
                <div>
                    <h1 className="buyer-explore-title">Explore Shops</h1>
                    <p className="buyer-explore-subtitle">Discover amazing local shops in your campus</p>
                </div>
                <div className="buyer-explore-stats">
                    <span className="buyer-shops-count">{shops.length} shops found</span>
                </div>
            </div>
            
            <FilterSearch 
                searchTerm={searchTerm}
                setSearchTerm={setSearchTerm}
                selectedCategory={selectedCategory}
                setSelectedCategory={setSelectedCategory}
                categories={categories}
            />
            
            <div className="buyer-shops-grid">
                {shops.map((shop) => (
                    <ShopCard 
                        key={shop.id} 
                        shop={shop} 
                        onVisitShop={handleVisitShop}
                    />
                ))}
            </div>
            
            {shops.length === 0 && !loading && (
                <div className="buyer-no-results">
                    <i className="fa-solid fa-search buyer-no-results-icon"></i>
                    <h3>No shops found</h3>
                    <p>Try adjusting your search terms or category filter</p>
                </div>
            )}
            
            <MenuModal 
                shop={selectedShop}
                isOpen={showMenuModal}
                onClose={() => {
                    setShowMenuModal(false);
                    setSelectedShop(null);
                }}
                onAddToCart={handleAddToCart}
            />
        </div>
    );
}

export default ExploreShopsContent;
