import React, { useState } from 'react';
import { Star } from 'lucide-react';
import '@fortawesome/fontawesome-free/css/all.min.css';

// Helper: Get current user ID
const getCurrentUserId = () => {
    const user = localStorage.getItem('user');
    if (user) {
        const userData = JSON.parse(user);
        return userData.id || userData._id || userData.userId;
    }
    return localStorage.getItem('userId');
};

// Helper: Get cart
const getUserCartItems = () => {
    const userId = getCurrentUserId();
    const storedCart = localStorage.getItem(`cart_${userId}`);
    try {
        const parsed = storedCart ? JSON.parse(storedCart) : [];
        return parsed;
    } catch (e) {
        console.error('❌ Failed to parse cart:', e);
        return [];
    }
};

function CartItem({ item, onQuantityChange, onRemove }) {
    const handleQuantityChange = (newQuantity) => {
        if (newQuantity >= 1) {
            onQuantityChange(item._id, newQuantity);
        }
    };

    const handleRemove = () => {
        onRemove(item._id);
    };

    const handlePlaceOrder = () => {
        alert(`Placing order for ${item.itemName}...`);
        // TODO: Integrate backend call here
    };

    return (
        <div className={`cart-item ${item.availability === false ? 'out-of-stock' : ''}`} style={{
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'space-between',
            padding: '12px 16px',
            borderBottom: '1px solid #eee',
            borderRadius: '10px',
            backgroundColor: '#fff',
            marginBottom: '10px',
            boxShadow: '0 2px 5px rgba(0,0,0,0.05)'
        }}>
            <div style={{ flex: 1 }}>
                <h3 style={{ margin: 0, fontSize: '16px' }}>{item.itemName}</h3>
                <span style={{ fontWeight: 'bold', color: '#333' }}>Rs {item.price}</span>
                <span style={{ margin: '0 10px' }}>|</span>
                <span>Qty: {item.quantity}</span>
            </div>

            <div style={{ display: 'flex', alignItems: 'center', gap: '10px' }}>
                <button onClick={() => handleQuantityChange(item.quantity - 1)} className="qty-btn">-</button>
                <span>{item.quantity}</span>
                <button onClick={() => handleQuantityChange(item.quantity + 1)} className="qty-btn">+</button>

                <button onClick={handleRemove} style={{
                    marginLeft: '10px',
                    background: 'transparent',
                    color: 'red',
                    border: 'none',
                    cursor: 'pointer'
                }}>
                    <i className="fa-solid fa-trash"></i>
                </button>

                <button
                    onClick={handlePlaceOrder}
                    style={{
                        backgroundColor: '#16a34a',
                        color: 'white',
                        padding: '6px 12px',
                        borderRadius: '6px',
                        border: 'none',
                        fontSize: '14px',
                        fontWeight: 'bold',
                        cursor: 'pointer',
                        whiteSpace: 'nowrap'
                    }}
                >
                    Place Order
                </button>
            </div>
        </div>
    );
}

function MyCartContent() {
    const [cartItemsState, setCartItemsState] = useState(getUserCartItems());

    const updateUserCartStorage = (updatedItems) => {
        const userId = getCurrentUserId();
        if (userId) {
            localStorage.setItem(`cart_${userId}`, JSON.stringify(updatedItems));
        }
    };

    const handleQuantityChange = (itemId, newQuantity) => {
        const updatedItems = cartItemsState.map(item =>
            item._id === itemId ? { ...item, quantity: newQuantity } : item
        );
        setCartItemsState(updatedItems);
        updateUserCartStorage(updatedItems);
    };

    const handleRemoveItem = (itemId) => {
        const updatedItems = cartItemsState.filter(item => item._id !== itemId);
        setCartItemsState(updatedItems);
        updateUserCartStorage(updatedItems);
    };

    const handleClearCart = () => {
        const userId = getCurrentUserId();
        setCartItemsState([]);
        if (userId) {
            localStorage.removeItem(`cart_${userId}`);
        }
    };

    if (cartItemsState.length === 0) {
        return (
            <div className="buyer-cart-content">
                <h1 className="buyer-cart-title">My Cart</h1>
                <p>Your cart is empty.</p>
            </div>
        );
    }

    return (
        <div className="buyer-cart-content" style={{ padding: '20px' }}>
            <div className="buyer-cart-header" style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '20px' }}>
                <h1>My Cart</h1>
                <button
                    onClick={handleClearCart}
                    style={{
                        backgroundColor: '#ef4444',
                        color: '#fff',
                        border: 'none',
                        padding: '8px 14px',
                        borderRadius: '6px',
                        cursor: 'pointer'
                    }}
                >
                    Clear Cart
                </button>
            </div>

            {cartItemsState.map(item => (
                <CartItem
                    key={item._id}
                    item={item}
                    onQuantityChange={handleQuantityChange}
                    onRemove={handleRemoveItem}
                />
            ))}
        </div>
    );
}

export default MyCartContent;
