import React, { useState, useEffect } from 'react';
import { Heart, Search, Filter, Star, MapPin, Clock, Trash2 } from 'lucide-react';
import '@fortawesome/fontawesome-free/css/all.min.css';

// Favorites management functions (same as in ExploreShops)
const getFavorites = () => {
    const favorites = localStorage.getItem('favoriteShops');
    return favorites ? JSON.parse(favorites) : [];
};

const removeFromFavorites = (shopId) => {
    const favorites = getFavorites();
    const updatedFavorites = favorites.filter(fav => fav.id !== shopId);
    localStorage.setItem('favoriteShops', JSON.stringify(updatedFavorites));
};

function FavoriteShopCard({ shop, onRemove }) {
    const handleRemove = () => {
        removeFromFavorites(shop.id);
        onRemove(shop.id);
    };

    const renderStars = (rating) => {
        const full = Math.floor(rating);
        const half = rating % 1 >= 0.5;
        const empty = 5 - full - (half ? 1 : 0);
        return '★'.repeat(full) + (half ? '½' : '') + '☆'.repeat(empty);
    };

    return (
        <div className="buyer-favorite-shop-card">
            <div className="buyer-favorite-shop-image-container">
                <img src={shop.image} alt={shop.name} className="buyer-favorite-shop-image" />
                <div className="buyer-favorite-shop-category-tag">
                    {shop.category}
                </div>
            </div>
            
            <div className="buyer-favorite-shop-content">
                <div className="buyer-favorite-shop-header">
                    <h3 className="buyer-favorite-shop-name">{shop.name}</h3>
                    <button 
                        onClick={handleRemove}
                        className="buyer-favorite-remove-btn"
                        title="Remove from favorites"
                    >
                        <Heart 
                            size={20} 
                            fill="#f44336" 
                            color="#f44336"
                        />
                    </button>
                </div>
                
                <p className="buyer-favorite-shop-description">{shop.description}</p>
                
                <div className="buyer-favorite-shop-rating">
                    <Star className="buyer-star-icon" fill="#fbbf24" color="#fbbf24" size={16} />
                    <span className="buyer-rating-value">{shop.rating}</span>
                    <span className="buyer-rating-stars">({renderStars(shop.rating)})</span>
                </div>
                
                <div className="buyer-favorite-shop-meta">
                    <div className="buyer-favorite-shop-items">
                        <i className="fa-solid fa-utensils"></i>
                        <span>{shop.totalItems} items</span>
                    </div>
                    <div className="buyer-favorite-shop-delivery">
                        <Clock size={14} />
                        <span>{shop.deliveryTime}</span>
                    </div>
                </div>
            </div>
        </div>
    );
}

function FavoriteSection() {
    const [favoriteShops, setFavoriteShops] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');

    // Load favorites on component mount
    useEffect(() => {
        loadFavorites();
    }, []);

    const loadFavorites = () => {
        const favorites = getFavorites();
        setFavoriteShops(favorites);
    };

    const handleRemoveShop = (shopId) => {
        setFavoriteShops(prev => prev.filter(shop => shop.id !== shopId));
    };

    const handleClearAllFavorites = () => {
        if (window.confirm('Are you sure you want to remove all favorite shops?')) {
            localStorage.removeItem('favoriteShops');
            setFavoriteShops([]);
        }
    };

    // Filter shops based on search
    const filteredShops = favoriteShops.filter(shop =>
        shop.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        shop.category.toLowerCase().includes(searchTerm.toLowerCase()) ||
        shop.description.toLowerCase().includes(searchTerm.toLowerCase())
    );

    return (
        <div className="buyer-favorite-section-wrapper">
            <div className="buyer-favorite-header">
                <div>
                    <h1 className="buyer-section-heading">Favorite Shops</h1>
                    <p className="buyer-favorite-subtitle">Your saved shops for quick access</p>
                </div>
                <div className="buyer-favorite-stats">
                    <span className="buyer-favorite-count">{favoriteShops.length} favorite shops</span>
                    {favoriteShops.length > 0 && (
                        <button 
                            onClick={handleClearAllFavorites}
                            className="buyer-clear-all-btn"
                        >
                            <Trash2 size={16} />
                            Clear All
                        </button>
                    )}
                </div>
            </div>

            {favoriteShops.length > 0 && (
                <div className="buyer-favorite-search-container">
                    <div className="buyer-search-input-wrapper">
                        <Search className="buyer-search-icon" size={20} />
                        <input
                            type="text"
                            placeholder="Search favorite shops..."
                            value={searchTerm}
                            onChange={(e) => setSearchTerm(e.target.value)}
                            className="buyer-search-input"
                        />
                    </div>
                </div>
            )}

            <div className="buyer-favorite-content">
                {favoriteShops.length === 0 ? (
                    <div className="buyer-no-favorites">
                        <div className="buyer-no-favorites-icon">
                            <Heart size={64} color="#ccc" />
                        </div>
                        <h3>No favorite shops yet</h3>
                        <p>Start exploring shops and add them to your favorites by clicking the heart icon!</p>
                    </div>
                ) : filteredShops.length === 0 ? (
                    <div className="buyer-no-results">
                        <i className="fa-solid fa-search buyer-no-results-icon"></i>
                        <h3>No shops found</h3>
                        <p>Try adjusting your search terms</p>
                    </div>
                ) : (
                    <div className="buyer-favorite-shops-grid">
                        {filteredShops.map((shop) => (
                            <FavoriteShopCard 
                                key={shop.id} 
                                shop={shop} 
                                onRemove={handleRemoveShop}
                            />
                        ))}
                    </div>
                )}
            </div>
        </div>
    );
}

export default FavoriteSection;
