import React from 'react';
import { Heart, Search, Filter, Star, MapPin } from 'lucide-react';
import { useState } from 'react';
import '@fortawesome/fontawesome-free/css/all.min.css'; // Import Font Awesome
import TimePicker from 'react-time-picker';
import 'react-time-picker/dist/TimePicker.css';
import 'react-clock/dist/Clock.css';
import styles from './buyer.module.css';
import ExploreShopsContent from "./buyer-dashboard/ExploreShopsContent"
import MyCartContent from "./buyer-dashboard/MyCartContent"
import NotificationsComponent from "./buyer-dashboard/NotificationsComponent"
import BuyerSettings from "./buyer-dashboard/BuyerSettings"
import FavoriteSection from "./buyer-dashboard/FavoriteSection"
import DashboardContent from "./buyer-dashboard/DashboardContent"


// Sidebar Component
function Sidebar({ activeMenu, setActiveMenu }) {
    const menuItems = [
        { name: 'Dashboard', icon: 'fa-tachometer-alt' },
        { name: 'Explore Shops', icon: 'fa-store' },
        { name: 'My Cart', icon: 'fa-shopping-cart' },
        { name: 'Favorites', icon: 'fa-heart' },
        { name: 'Notifications', icon: 'fa-bell' },
        { name: 'Settings', icon: 'fa-cog' },
    ];

    return (
        <aside className={styles.sidebar}>
            <div className={styles.sidebarbrand}>
                <span className={styles.navbarbrand}>LocalMart</span>
            </div>
            <ul className={styles.sidebarmenu}>
                {menuItems.map((item, index) => (
                    <li key={index}>
                        <a 
                            href="#" 
                            className={`styles.sidebar-link ${item.name === activeMenu ? 'active' : ''}`}
                            onClick={(e) => {
                                e.preventDefault();
                                setActiveMenu(item.name);
                            }}
                        >
                            <i className={`fa-solid ${item.icon}`}></i>
                            <span>{item.name}</span>
                        </a>
                    </li>
                ))}
            </ul>
            <div className={styles.sidebarfooter}>
                <div className={styles.userinfo}>
                    <i className="fa-solid fa-user-circle"></i>
                    <span>John Doe</span>
                </div>
                <a href="#" className={styles.logoutbutton}>
                    <i className="fa-solid fa-sign-out-alt"></i>
                    <span>Logout</span>
                </a>
            </div>
        </aside>
    );
}

// Special Offers Component
function SpecialOffers() {
    return (
        <div className={styles.special-offers}>
            <h2 className={styles.offer-title}>Special Offers:</h2>
            <p className={styles.offer-description}>20% off all food items this weekend!</p>
            <img src="https://via.placeholder.com/600x150" alt="Special Offer" className="offer-image" />
        </div>
    );
}

// Footer Component
function Footer() {
    return (
        <footer className={styles.footer}>
            <div className={styles.footer-content}>
                <span className={styles.footer-logo}>L</span>
                <span className={styles.footer-text}>LOCALMART - YOUR LOCAL MARKETPLACE</span>
            </div>
        </footer>
    );
}

function BuyerDashboard() {
    const [activeMenu, setActiveMenu] = useState('Dashboard');
    
    const renderContent = () => {
    switch(activeMenu) {
        case 'Explore Shops':
            return <ExploreShopsContent />;
        case 'My Cart':
            return <MyCartContent />;
        case 'Notifications':
            return <NotificationsComponent/>
        case 'Settings':
            return < BuyerSettings/>
        case 'Favorites':
            return <FavoriteSection/> 
        case 'Dashboard':
        default:
            return <DashboardContent />;
    }
};
    
    return (
        <div className={styles.app-container}>
            <div className={styles.main-container}>
                <Sidebar activeMenu={activeMenu} setActiveMenu={setActiveMenu} />
                <div className={styles.content-area}>
                    {renderContent()}
                </div>
            </div>
        </div>
    );
}
export default BuyerDashboard;
