// USER-SPECIFIC FAVORITES MANAGEMENT FUNCTIONS
const getCurrentUserId = () => {
    const user = localStorage.getItem('user');
    if (user) {
        const userData = JSON.parse(user);
        return userData.id || userData._id || userData.userId;
    }
    return localStorage.getItem('userId');
};

const getFavorites = () => {
    const userId = getCurrentUserId();
    if (!userId) return [];
    
    const favoritesKey = `favoriteShops_${userId}`;
    const favorites = localStorage.getItem(favoritesKey);
    return favorites ? JSON.parse(favorites) : [];
};

const saveFavorites = (favorites) => {
    const userId = getCurrentUserId();
    if (!userId) return;
    
    const favoritesKey = `favoriteShops_${userId}`;
    localStorage.setItem(favoritesKey, JSON.stringify(favorites));
};

const addToFavorites = (shop) => {
    const userId = getCurrentUserId();
    if (!userId) {
        console.warn('No user logged in');
        return false;
    }
    
    const favorites = getFavorites();
    const isAlreadyFavorite = favorites.some(fav => fav.id === shop.id);
    
    if (!isAlreadyFavorite) {
        const favoriteShop = {
            id: shop.id,
            name: shop.name,
            description: shop.businessAddress,
            image: shopImages[Math.floor(Math.random() * shopImages.length)],
            rating: shop.rating,
            category: Object.keys(shop.mainCategory)[0] || 'General',
            totalItems: shop.totalItems,
            deliveryTime: shop.deliveryTime
        };
        const updatedFavorites = [...favorites, favoriteShop];
        saveFavorites(updatedFavorites);
        return true;
    }
    return false;
};

const removeFromFavorites = (shopId) => {
    const favorites = getFavorites();
    const updatedFavorites = favorites.filter(fav => fav.id !== shopId);
    saveFavorites(updatedFavorites);
};

const isShopFavorite = (shopId) => {
    const favorites = getFavorites();
    return favorites.some(fav => fav.id === shopId);
};

// ORDER API FUNCTIONS
const orderAPI = {
    async createOrder(orderData) {
        const token = localStorage.getItem('token');
        const response = await fetch('http://localhost:5000/api/orders', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${token}`
            },
            body: JSON.stringify(orderData)
        });
        
        const data = await response.json();
        if (!response.ok) {
            throw new Error(data.message || 'Failed to create order');
        }
        return data;
    },

    async getBuyerOrders() {
        const token = localStorage.getItem('token');
        const response = await fetch('http://localhost:5000/api/orders/buyer', {
            headers: {
                'Authorization': `Bearer ${token}`
            }
        });
        
        const data = await response.json();
        if (!response.ok) {
            throw new Error(data.message || 'Failed to fetch orders');
        }
        return data.data;
    }
};

// ORDER FORM MODAL COMPONENT
function OrderFormModal({ cart, shopInfo, onClose, onOrderSuccess }) {
    const [orderForm, setOrderForm] = useState({
        deliveryAddress: '',
        phoneNumber: '',
        orderNotes: ''
    });
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');

    const total = cart.reduce((sum, item) => sum + (item.price * item.quantity), 0);

    const handleSubmit = async (e) => {
        e.preventDefault();
        
        if (!orderForm.deliveryAddress.trim() || !orderForm.phoneNumber.trim()) {
            setError('Please fill in all required fields');
            return;
        }

        setLoading(true);
        setError('');

        try {
            const orderData = {
                sellerId: shopInfo.id,
                items: cart.map(item => ({
                    menuItemId: item._id,
                    quantity: item.quantity
                })),
                deliveryAddress: orderForm.deliveryAddress,
                phoneNumber: orderForm.phoneNumber,
                orderNotes: orderForm.orderNotes
            };

            const result = await orderAPI.createOrder(orderData);
            onOrderSuccess(result.data);
        } catch (err) {
            setError(err.message);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="receipt-overlay">
            <div className="receipt-modal" style={{ maxWidth: '500px' }}>
                <div className="receipt-header">
                    <h3>Complete Your Order - {shopInfo.name}</h3>
                    <button onClick={onClose} className="receipt-close-btn">
                        <X size={20} />
                    </button>
                </div>

                <form onSubmit={handleSubmit}>
                    <div className="receipt-items">
                        {cart.map((item) => (
                            <div key={item._id} className="receipt-item">
                                <div className="receipt-item-info">
                                    <span className="receipt-item-name">{item.itemName}</span>
                                    <span className="receipt-item-quantity">× {item.quantity}</span>
                                </div>
                                <span className="receipt-item-price">Rs {(item.price * item.quantity).toFixed(2)}</span>
                            </div>
                        ))}
                    </div>

                    <div className="receipt-total">
                        <strong>Total: Rs {total.toFixed(2)}</strong>
                    </div>

                    <div style={{ padding: '20px 0' }}>
                        <div style={{ marginBottom: '15px' }}>
                            <label style={{ display: 'block', marginBottom: '5px', fontWeight: 'bold' }}>
                                Delivery Address *
                            </label>
                            <textarea
                                value={orderForm.deliveryAddress}
                                onChange={(e) => setOrderForm({ ...orderForm, deliveryAddress: e.target.value })}
                                placeholder="Enter your complete delivery address"
                                required
                                style={{
                                    width: '100%',
                                    padding: '10px',
                                    border: '1px solid #ddd',
                                    borderRadius: '5px',
                                    minHeight: '60px',
                                    resize: 'vertical'
                                }}
                            />
                        </div>

                        <div style={{ marginBottom: '15px' }}>
                            <label style={{ display: 'block', marginBottom: '5px', fontWeight: 'bold' }}>
                                Phone Number *
                            </label>
                            <input
                                type="tel"
                                value={orderForm.phoneNumber}
                                onChange={(e) => setOrderForm({ ...orderForm, phoneNumber: e.target.value })}
                                placeholder="+92-300-1234567"
                                required
                                style={{
                                    width: '100%',
                                    padding: '10px',
                                    border: '1px solid #ddd',
                                    borderRadius: '5px'
                                }}
                            />
                        </div>

                        <div style={{ marginBottom: '15px' }}>
                            <label style={{ display: 'block', marginBottom: '5px', fontWeight: 'bold' }}>
                                Order Notes (Optional)
                            </label>
                            <textarea
                                value={orderForm.orderNotes}
                                onChange={(e) => setOrderForm({ ...orderForm, orderNotes: e.target.value })}
                                placeholder="Any special instructions (e.g., extra spicy, no onions)"
                                style={{
                                    width: '100%',
                                    padding: '10px',
                                    border: '1px solid #ddd',
                                    borderRadius: '5px',
                                    minHeight: '50px',
                                    resize: 'vertical'
                                }}
                                maxLength={500}
