                        <button 
                            type="submit" 
                            className="receipt-confirm-btn"
                            disabled={loading}
                        >
                            {loading ? (
                                <>
                                    <div className="loading-spinner" style={{ width: '16px', height: '16px', marginRight: '8px' }}></div>
