        switch (status) {
            case 'pending': return <Clock size={16} color="#f59e0b" />;
            case 'confirmed': return <CheckCircle size={16} color="#10b981" />;
            case 'preparing': return <Package size={16} color="#3b82f6" />;
            case 'ready': return <CheckCircle size={16} color="#10b981" />;
            case 'delivered': return <Truck size={16} color="#10b981" />;
