import React from 'react';
import { useNavigate, useLocation } from 'react-router-dom';

const Navbar = () => {
  const navigate = useNavigate();
  const location = useLocation();

  const handleScrollTo = (sectionId) => {
    if (location.pathname === '/') {
      // Already on home, scroll smoothly
      const section = document.getElementById(sectionId);
      if (section) {
        section.scrollIntoView({ behavior: 'smooth' });
      }
    } else {
      // Navigate to home page with hash
      navigate(`/#${sectionId}`);
    }
  };

  return (
    <nav style={styles.navbar}>
      <div style={styles.logo} onClick={() => navigate('/')}>
        LocalMart
      </div>
      <ul style={styles.navLinks}>
        <li>
          <button style={styles.navButton} onClick={() => handleScrollTo('about')}>
            About
          </button>
        </li>
        <li>
          <button style={styles.navButton} onClick={() => handleScrollTo('services')}>
            Services
          </button>
        </li>
        <li>
          <button style={styles.navButton} onClick={() => handleScrollTo('contact')}>
            Contact
          </button>
        </li>
        {/* Add other nav items as needed */}
      </ul>
    </nav>
  );
};

const styles = {
  navbar: {
    position: 'sticky',  // makes it sticky when scrolling
    top: 0,
    backgroundColor: '#fff',
    padding: '1rem 2rem',
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    boxShadow: '0 2px 5px rgba(0,0,0,0.1)',
    zIndex: 1000,
  },
  logo: {
    fontWeight: 'bold',
    fontSize: '1.5rem',
    cursor: 'pointer',
  },
  navLinks: {
    listStyle: 'none',
    display: 'flex',
    gap: '1.5rem',
    margin: 0,
    padding: 0,
  },
  navButton: {
    background: 'none',
    border: 'none',
    cursor: 'pointer',
    fontSize: '1rem',
    padding: 0,
    color: '#333',
  },
};

export default Navbar;
