import React, { useState, useEffect, createContext, useContext } from 'react';
import { Heart, Search, Filter, Star, MapPin, ShoppingBag, ShoppingCart, Users, TrendingUp } from 'lucide-react';

// API Service Functions (Replace with your actual API endpoints)
const apiService = {
  // Replace these URLs with your actual backend URLs
  baseURL: 'http://localhost:5000/api',
  
  // User data
  async getUserStats(userId) {
    try {
      const response = await fetch(`${this.baseURL}/auth/profile/${userId}`, {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`,
          'Content-Type': 'application/json'
        }
      });
      return await response.json();
    } catch (error) {
      console.error('Error fetching user stats:', error);
      // Return mock data for demonstration
      return {
        success: true,
        data: {
          name: 'John Doe',
          totalOrders: 24,
          cartItems: 5,
          favoriteShops: 12,
          reviewsGiven: 18
        }
      };
    }
  },

  // Orders data
  async getUserOrders(userId, limit = 4) {
    try {
      const response = await fetch(`${this.baseURL}/orders/user/${userId}?limit=${limit}`, {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`,
          'Content-Type': 'application/json'
        }
      });
      return await response.json();
    } catch (error) {
      console.error('Error fetching orders:', error);
      // Return mock data for demonstration
      return {
        success: true,
        data: [
          {
            _id: '1',
            orderId: '#ORD-2025-1234',
            shop: { name: 'Campus Bites' },
            createdAt: '2025-04-27T10:00:00Z',
            totalAmount: 450,
            status: 'delivered'
          },
          {
            _id: '2',
            orderId: '#ORD-2025-1233',
            shop: { name: 'Artistic Creations' },
            createdAt: '2025-04-25T15:30:00Z',
            totalAmount: 850,
            status: 'pending'
          }
        ]
      };
    }
  },

  // Featured shops
  async getFeaturedShops(limit = 3) {
    try {
      const response = await fetch(`${this.baseURL}/shops/featured?limit=${limit}`, {
        headers: {
          'Content-Type': 'application/json'
        }
      });
      return await response.json();
    } catch (error) {
      console.error('Error fetching featured shops:', error);
      // Return mock data for demonstration
      return {
        success: true,
        data: [
          {
            _id: '1',
            name: 'Campus Bites',
            category: 'Food',
            rating: 4.2,
            reviewCount: 46,
            description: 'Homemade meals and snacks for students. Daily specials and customizable menu options.',
            image: 'https://via.placeholder.com/150'
          },
          {
            _id: '2',
            name: 'Artistic Creations',
            category: 'Crafts',
            rating: 4.5,
            reviewCount: 28,
            description: 'Handmade crafts, calligraphy, and art pieces. Custom orders available.',
            image: 'https://via.placeholder.com/150'
          }
        ]
      };
    }
  },

  // Toggle favorite
  async toggleFavorite(userId, shopId) {
    try {
      const response = await fetch(`${this.baseURL}/auth/favorites`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ shopId })
      });
      return await response.json();
    } catch (error) {
      console.error('Error toggling favorite:', error);
      return { success: false };
    }
  }
};

// Context for Dashboard Data
const DashboardContext = createContext();

const DashboardProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [userStats, setUserStats] = useState(null);
  const [recentOrders, setRecentOrders] = useState([]);
  const [featuredShops, setFeaturedShops] = useState([]);
  const [favorites, setFavorites] = useState(new Set());
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Get current user (replace with your auth logic)
  const getCurrentUser = () => {
    // This should come from your auth context/localStorage
    return {
      id: localStorage.getItem('userId') || 'demo-user-id',
      name: localStorage.getItem('userName') || 'John Doe'
    };
  };

  // Load dashboard data
  const loadDashboardData = async () => {
    try {
      setLoading(true);
      setError(null);
      
      const currentUser = getCurrentUser();
      setUser(currentUser);

      // Fetch all dashboard data concurrently
      const [statsResponse, ordersResponse, shopsResponse] = await Promise.all([
        apiService.getUserStats(currentUser.id),
        apiService.getUserOrders(currentUser.id),
        apiService.getFeaturedShops()
      ]);

      if (statsResponse.success) {
        setUserStats(statsResponse.data);
      }

      if (ordersResponse.success) {
        setRecentOrders(ordersResponse.data);
      }

      if (shopsResponse.success) {
        setFeaturedShops(shopsResponse.data);
      }

    } catch (err) {
      setError('Failed to load dashboard data');
      console.error('Dashboard data error:', err);
    } finally {
      setLoading(false);
    }
  };

  // Toggle favorite shop
  const toggleFavorite = async (shopId) => {
    try {
      const response = await apiService.toggleFavorite(user.id, shopId);
      if (response.success) {
        setFavorites(prev => {
          const newFavorites = new Set(prev);
          if (newFavorites.has(shopId)) {
            newFavorites.delete(shopId);
          } else {
            newFavorites.add(shopId);
          }
          return newFavorites;
        });
        
        // Update stats
        setUserStats(prev => ({
          ...prev,
          favoriteShops: favorites.has(shopId) ? prev.favoriteShops - 1 : prev.favoriteShops + 1
        }));
      }
    } catch (err) {
      console.error('Error toggling favorite:', err);
    }
  };

  useEffect(() => {
    loadDashboardData();
  }, []);

  const value = {
    user,
    userStats,
    recentOrders,
    featuredShops,
    favorites,
    loading,
    error,
    toggleFavorite,
    refreshData: loadDashboardData
  };

  return (
    <DashboardContext.Provider value={value}>
      {children}
    </DashboardContext.Provider>
  );
};

// Custom hook to use dashboard context
const useDashboard = () => {
  const context = useContext(DashboardContext);
  if (!context) {
    throw new Error('useDashboard must be used within DashboardProvider');
  }
  return context;
};

// Seller Card Component
function SellerCard({ seller }) {
  const { favorites, toggleFavorite } = useDashboard();
  const isFavorite = favorites.has(seller._id);
  
  const handleFavoriteClick = (e) => {
    e.stopPropagation();
    toggleFavorite(seller._id);
  };
  
  const renderStars = (rating) => {
    const stars = [];
    const fullStars = Math.floor(rating);
    const hasHalfStar = rating % 1 !== 0;
    
    for (let i = 0; i < fullStars; i++) {
      stars.push(<Star key={i} size={16} fill="#ffd700" color="#ffd700" />);
    }
    
    if (hasHalfStar) {
      stars.push(<Star key="half" size={16} fill="#ffd700" color="#ffd700" style={{ opacity: 0.5 }} />);
    }
    
    return stars;
  };
  
  return (
    <div className="bg-white p-6 rounded-lg shadow-md hover:shadow-lg transition-shadow">
      <div className="flex justify-between items-start mb-3">
        <span className="bg-blue-100 text-blue-800 text-xs px-2 py-1 rounded-full">
          {seller.category}
        </span>
        <button 
          onClick={handleFavoriteClick}
          className="p-1 hover:bg-gray-100 rounded-full transition-colors"
        >
          <Heart 
            size={20}
            fill={isFavorite ? "#f44336" : "none"} 
            color={isFavorite ? "#f44336" : "currentColor"}
          />
        </button>
      </div>
      
      <h3 className="text-lg font-semibold mb-2">{seller.name}</h3>
      
      <div className="flex items-center mb-3">
        <div className="flex items-center mr-2">
          {renderStars(seller.rating)}
        </div>
        <span className="text-sm text-gray-600">({seller.reviewCount} reviews)</span>
      </div>
      
      <p className="text-gray-600 text-sm mb-4 line-clamp-2">{seller.description}</p>
      
      <button className="w-full bg-blue-600 text-white py-2 px-4 rounded-lg hover:bg-blue-700 transition-colors">
        View {seller.category === 'Food' ? 'Menu' : 'Products'}
      </button>
    </div>
  );
}

// Recent Orders Component
function RecentOrders() {
  const { recentOrders } = useDashboard();
  
  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric'
    });
  };
  
  const getStatusColor = (status) => {
    switch (status.toLowerCase()) {
      case 'delivered': return 'bg-green-100 text-green-800';
      case 'pending': return 'bg-yellow-100 text-yellow-800';
      case 'cancelled': return 'bg-red-100 text-red-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };
  
  if (recentOrders.length === 0) {
    return (
      <div className="bg-white p-6 rounded-lg shadow-md">
        <h2 className="text-xl font-semibold mb-4">Recent Orders</h2>
        <p className="text-gray-500 text-center py-8">No recent orders found</p>
      </div>
    );
  }
  
  return (
    <div className="bg-white p-6 rounded-lg shadow-md">
      <div className="flex justify-between items-center mb-4">
        <h2 className="text-xl font-semibold">Recent Orders</h2>
        <button className="text-blue-600 hover:text-blue-800 text-sm font-medium">
          View All
        </button>
      </div>
      
      <div className="overflow-x-auto">
        <table className="w-full">
          <thead>
            <tr className="border-b">
              <th className="text-left py-2 text-sm font-medium text-gray-600">Order ID</th>
              <th className="text-left py-2 text-sm font-medium text-gray-600">Seller</th>
              <th className="text-left py-2 text-sm font-medium text-gray-600">Date</th>
              <th className="text-left py-2 text-sm font-medium text-gray-600">Amount</th>
              <th className="text-left py-2 text-sm font-medium text-gray-600">Status</th>
            </tr>
          </thead>
          <tbody>
            {recentOrders.map((order) => (
              <tr key={order._id} className="border-b hover:bg-gray-50">
                <td className="py-3">
                  <button className="text-blue-600 hover:text-blue-800 font-medium">
                    {order.orderId}
                  </button>
                </td>
                <td className="py-3">{order.shop?.name || 'Unknown'}</td>
                <td className="py-3 text-gray-600">{formatDate(order.createdAt)}</td>
                <td className="py-3 font-medium">₹{order.totalAmount}</td>
                <td className="py-3">
                  <span className={`px-2 py-1 rounded-full text-xs ${getStatusColor(order.status)}`}>
                    {order.status.charAt(0).toUpperCase() + order.status.slice(1)}
                  </span>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

// Stats Card Component
function StatsCard({ icon: Icon, title, value, trend }) {
  return (
    <div className="bg-white p-6 rounded-lg shadow-md">
      <div className="flex items-center justify-between">
        <div>
          <p className="text-sm font-medium text-gray-600">{title}</p>
          <p className="text-2xl font-bold text-gray-900">{value}</p>
          {trend && (
            <p className="text-sm text-green-600 flex items-center mt-1">
              <TrendingUp size={16} className="mr-1" />
              {trend}
            </p>
          )}
        </div>
        <div className="p-3 bg-blue-100 rounded-full">
          <Icon size={24} className="text-blue-600" />
        </div>
      </div>
    </div>
  );
}

// Loading Component
function LoadingSpinner() {
  return (
    <div className="flex justify-center items-center py-12">
      <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
    </div>
  );
}

// Error Component
function ErrorMessage({ message, onRetry }) {
  return (
    <div className="text-center py-12">
      <p className="text-red-600 mb-4">{message}</p>
      <button 
        onClick={onRetry}
        className="bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700"
      >
        Try Again
      </button>
    </div>
  );
}

// Main Dashboard Content Component
function DashboardContent() {
  const { user, userStats, featuredShops, loading, error, refreshData } = useDashboard();
  
  if (loading) {
    return <LoadingSpinner />;
  }
  
  if (error) {
    return <ErrorMessage message={error} onRetry={refreshData} />;
  }
  
  return (
    <div className="p-6 max-w-7xl mx-auto">
      {/* Welcome Section */}
      <div className="mb-8 flex flex-col md:flex-row md:items-center md:justify-between">
        <div>
          <h1 className="text-3xl font-bold text-gray-900">
            Welcome back, {user?.name || 'User'}!
          </h1>
          <p className="text-gray-600 mt-1">
            Here's what's happening with your orders today.
          </p>
        </div>
        <div className="flex gap-3 mt-4 md:mt-0">
          <button className="px-4 py-2 bg-gray-100 text-gray-700 rounded-lg hover:bg-gray-200">
            View Cart
          </button>
          <button className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700">
            + New Order
          </button>
        </div>
      </div>
      
      {/* Stats Grid */}
      {userStats && (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
          <StatsCard
            icon={ShoppingBag}
            title="Total Orders"
            value={userStats.totalOrders || 0}
            trend="+12% from last month"
          />
          <StatsCard
            icon={ShoppingCart}
            title="Cart Items"
            value={userStats.cartItems || 0}
          />
          <StatsCard
            icon={Heart}
            title="Favorite Sellers"
            value={userStats.favoriteShops || 0}
          />
          <StatsCard
            icon={Star}
            title="Reviews Given"
            value={userStats.reviewsGiven || 0}
          />
        </div>
      )}
      
      {/* Featured Sellers */}
      {featuredShops.length > 0 && (
        <div className="mb-8">
          <div className="flex justify-between items-center mb-6">
            <h2 className="text-2xl font-semibold text-gray-900">Featured Sellers</h2>
            <button className="text-blue-600 hover:text-blue-800 font-medium">
              View All
            </button>
          </div>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {featuredShops.map((seller) => (
              <SellerCard key={seller._id} seller={seller} />
            ))}
          </div>
        </div>
      )}
      
      {/* Recent Orders */}
      <RecentOrders />
    </div>
  );
}

// Main App Component
export default function App() {
  return (
    <DashboardProvider>
      <div className="min-h-screen bg-gray-50">
        <DashboardContent />
      </div>
    </DashboardProvider>
  );
}
