import React, { useEffect, useState } from "react";
import "./Order.css";
import "./seller.css";

// Get seller ID from localStorage
const getCurrentUserId = () => {
  const user = localStorage.getItem("user");
  if (user) {
    const userData = JSON.parse(user);
    return userData.id || userData._id || userData.userId;
  }
  return localStorage.getItem("userId");
};

function Order() {
  const [orders, setOrders] = useState([]);
  const [notification, setNotification] = useState({
    show: false,
    message: "",
    isSuccess: true,
  });
  const [loading, setLoading] = useState({ type: "", id: null });

  const sellerId = getCurrentUserId();
  console.log("Seller ID:", sellerId);

  // Helper to format status text nicely
  const formatStatus = (status) => {
    if (!status) return "";
    return status.charAt(0).toUpperCase() + status.slice(1);
  };

  // Show notification helper
  const showNotification = (message, isSuccess = true) => {
    setNotification({ show: true, message, isSuccess });
    setTimeout(() => {
      setNotification((prev) => ({ ...prev, show: false }));
    }, 3000);
  };

  // Fetch orders for the seller with token header
  const fetchOrders = async () => {
    try {
      const token = localStorage.getItem("token");
      const response = await fetch(`http://localhost:5000/api/orders/seller`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      const json = await response.json();

      if (!response.ok) throw new Error(json.message || "Failed to fetch orders");

      setOrders(json.data); // use the 'data' array from the response

      console.log("Fetched orders:", json.data);
    } catch (error) {
      showNotification(error.message, false);
    }
  };

  useEffect(() => {
    if (sellerId) {
      fetchOrders();
    }
  }, [sellerId]);

  // Update order status API call with token header
  const updateOrderStatus = async (orderId, newStatus) => {
    setLoading({ type: "order", id: orderId });
    try {
      const token = localStorage.getItem("token");
      const response = await fetch(`http://localhost:5000/api/orders/${orderId}`, {
        method: "PATCH",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ status: newStatus }),
      });
      if (!response.ok) {
        const errData = await response.json();
        throw new Error(errData.message || "Failed to update order status");
      }

      await fetchOrders();

      let message = "";
      if (newStatus === "accepted") {
        message = `Order #${orderId} accepted and being prepared!`;
      } else if (newStatus === "delivered") {
        message = `Order #${orderId} marked as delivered!`;
      }
      showNotification(message);
    } catch (error) {
      showNotification(error.message, false);
    } finally {
      setLoading({ type: "", id: null });
    }
  };

  // Button to view order details (replace with your modal/page)
  const viewOrderDetails = (orderId) => {
    showNotification(`Viewing details for Order #${orderId}`);
  };

  return (
    <div id="orders">
      <div className="header">
        <h1>All Orders</h1>
        <p>Manage and track all your customer orders here.</p>
      </div>

      <div className="orders">
        {(!Array.isArray(orders) || orders.length === 0) && <p>No orders found.</p>}
        {Array.isArray(orders) &&
          orders.map((order) => (
            <div key={order._id} className="order-card">
              <h3>
                Order #{order.orderNumber}{" "}
                <span className="order-status">{formatStatus(order.status)}</span>
              </h3>
              <div className="order-info">
                <p>
                  <strong>Customer:</strong> {order.buyer.name || order.buyer.email}
                </p>
                <p>
                  <strong>Address:</strong> {order.deliveryAddress}
                </p>
                <p>
                  <strong>Mobile:</strong> {order.phoneNumber}
                </p>
                <p>
                  <strong>Items:</strong>{" "}
                  {order.items.map((item) => `${item.itemName} x${item.quantity}`).join(", ")}
                </p>
                <p>
                  <strong>Total:</strong> Rs. {order.totalAmount}
                </p>
                <div className="action-buttons">
                  {/* Pending status: Accept + View Details */}
                  {order.status === "pending" && (
                    <>
                      <button
                        className="btn"
                        onClick={() => updateOrderStatus(order._id, "accepted")}
                        disabled={loading.type === "order" && loading.id === order._id}
                      >
                        {loading.type === "order" && loading.id === order._id ? (
                          <>
                            <div className="loading-spinner"></div> Updating...
                          </>
                        ) : (
                          "Accept Order"
                        )}
                      </button>
                      <button
                        className="btn btn-outline"
                        onClick={() => viewOrderDetails(order._id)}
                      >
                        View Details
                      </button>
                    </>
                  )}

                  {/* Accepted status: Deliver + View Details */}
                  {order.status === "accepted" && (
                    <>
                      <button
                        className="btn"
                        onClick={() => updateOrderStatus(order._id, "delivered")}
                        disabled={loading.type === "order" && loading.id === order._id}
                      >
                        {loading.type === "order" && loading.id === order._id ? (
                          <>
                            <div className="loading-spinner"></div> Updating...
                          </>
                        ) : (
                          "Deliver Order"
                        )}
                      </button>
                      <button
                        className="btn btn-outline"
                        onClick={() => viewOrderDetails(order._id)}
                      >
                        View Details
                      </button>
                    </>
                  )}

                  {/* Delivered or others: only View Details */}
                  {order.status !== "pending" && order.status !== "accepted" && (
                    <button
                      className="btn btn-outline"
                      onClick={() => viewOrderDetails(order._id)}
                    >
                      View Details
                    </button>
                  )}
                </div>
              </div>
            </div>
          ))}
      </div>

      {notification.show && (
        <div className={`notification ${notification.isSuccess ? "success" : "error"}`}>
          {notification.message}
        </div>
      )}
    </div>
  );
}

export default Order;
