import React, { useState } from 'react';
import { Star } from 'lucide-react';
import '@fortawesome/fontawesome-free/css/all.min.css';
import TimePicker from 'react-time-picker';
import 'react-time-picker/dist/TimePicker.css';
import 'react-clock/dist/Clock.css';

const getCurrentUserId = () => {
    const user = localStorage.getItem('user');
    if (user) {
        const userData = JSON.parse(user);
        return userData.id || userData._id || userData.userId;
    }
    return localStorage.getItem('userId');
};

const getUserCartItems = () => {
    const userId = getCurrentUserId();
    const storedCart = localStorage.getItem(`cart_${userId}`);
    try {
        const parsed = storedCart ? JSON.parse(storedCart) : [];
        console.log('ðŸ“¦ Loaded Cart Items:', parsed);
        return parsed;
    } catch (e) {
        console.error('âŒ Failed to parse cart:', e);
        return [];
    }
};

function OrderSummary({ items }) {
    const subtotal = items.filter(item => item.availability !== false).reduce((sum, item) => sum + (item.price * item.quantity), 0);
    const deliveryFee = 50;
    const total = subtotal + deliveryFee;
    const totalItems = items.reduce((sum, item) => sum + item.quantity, 0);

    return (
        <div className="buyer-order-summary">
            <h3 className="buyer-summary-title">Order Summary</h3>
            <div className="buyer-summary-row">
                <span>Items ({totalItems})</span>
                <span>Rs {subtotal}</span>
            </div>
            <div className="buyer-summary-row">
                <span>Delivery Fee</span>
                <span>Rs {deliveryFee}</span>
            </div>
            <div className="buyer-summary-divider"></div>
            <div className="buyer-summary-total">
                <span>Total Amount</span>
                <span>Rs {total}</span>
            </div>
        </div>
    );
}

function CartItem({ item, onQuantityChange, onRemove, onPlaceOrder }) {
    const handleQuantityChange = (newQuantity) => {
        if (newQuantity >= 1) {
            onQuantityChange(item._id, newQuantity);
        }
    };

    const handleRemove = () => {
        onRemove(item._id);
    };

    return (
        <div className={`cart-item ${item.availability === false ? 'out-of-stock' : ''}`} style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', border: '1px solid #ddd', padding: '16px', borderRadius: '8px', marginBottom: '16px', background: '#fff' }}>
            <div style={{ flex: 1 }}>
                <div style={{ display: 'flex', alignItems: 'center', gap: '12px' }}>
                    <h3 style={{ margin: 0, fontSize: '16px' }}>{item.itemName}</h3>
                    <span style={{ fontWeight: 'bold', color: '#333', fontSize: '16px' }}>Rs {item.price}</span>
                    <span style={{ fontSize: '15px' }}>Qty: {item.quantity}</span>
                </div>
                <div style={{ marginTop: '8px', fontSize: '13px', color: '#555' }}>
                    <p style={{ margin: 0 }}>From: {item.shopName || 'Shop'} | {item.category}</p>
                    <div style={{ display: 'flex', alignItems: 'center', gap: '6px' }}>
                        <Star fill="#fbbf24" color="#fbbf24" size={14} />
                        <span>{item.rating || 4.0}</span>
                        <span>({item.reviews || 10})</span>
                    </div>
                </div>
            </div>

            <div style={{ display: 'flex', alignItems: 'center', gap: '10px' }}>
                <button onClick={() => handleQuantityChange(item.quantity - 1)} className="buyer-quantity-btn">
                    <i className="fa-solid fa-minus"></i>
                </button>
                <span>{item.quantity}</span>
                <button onClick={() => handleQuantityChange(item.quantity + 1)} className="buyer-quantity-btn">
                    <i className="fa-solid fa-plus"></i>
                </button>
                <button onClick={handleRemove} style={{ background: 'transparent', color: 'red', border: 'none' }}>
                    <i className="fa-solid fa-trash"></i>
                </button>
                <button
                    onClick={() => onPlaceOrder(item)}
                    disabled={item.availability === false}
                    style={{ backgroundColor: '#22c55e', color: 'white', padding: '8px 12px', borderRadius: '6px', border: 'none', fontWeight: '500', fontSize: '14px', cursor: 'pointer' }}
                >
                    <i className="fa-solid fa-check" style={{ marginRight: '6px' }}></i>Place Order
                </button>
            </div>
        </div>
    );
}

function MyCartContent() {
    const [cartItemsState, setCartItemsState] = useState(getUserCartItems());

    const updateUserCartStorage = (updatedItems) => {
        const userId = getCurrentUserId();
        if (userId) {
            localStorage.setItem(`cart_${userId}`, JSON.stringify(updatedItems));
        }
    };

    const handleQuantityChange = (itemId, newQuantity) => {
        const updatedItems = cartItemsState.map(item =>
            item._id === itemId ? { ...item, quantity: newQuantity } : item
        );
        setCartItemsState(updatedItems);
        updateUserCartStorage(updatedItems);
    };

    const handleRemoveItem = (itemId) => {
        const updatedItems = cartItemsState.filter(item => item._id !== itemId);
        setCartItemsState(updatedItems);
        updateUserCartStorage(updatedItems);
    };

    const handlePlaceOrder = async (item) => {
        const token = localStorage.getItem('token');
        if (!token) {
            alert("Please log in first.");
            return;
        }

        const orderData = {
            sellerId: item.shopId || item.shop?._id || item.sellerId || "default_shop_id",
            items: [
                {
                    menuItemId: item._id,
                    quantity: item.quantity
                }
            ],
            deliveryAddress: "Chagda Hostel",
            phoneNumber: "N/A",
            orderNotes: ""
        };

        try {
            const res = await fetch("http://localhost:5000/api/orders", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                    Authorization: `Bearer ${token}`
                },
                body: JSON.stringify(orderData)
            });

            const data = await res.json();
            if (data.success) {
                alert("Order stored successfully!");
                console.log("Order:", data.data);
            } else {
                alert("Failed: " + data.message);
                console.log(data);
            }
        } catch (err) {
            console.error("Order error:", err);
            alert("Something went wrong.");
        }
    };

    const availableItems = cartItemsState.filter(item => item.availability !== false);
    const outOfStockItems = cartItemsState.filter(item => item.availability === false);

    if (cartItemsState.length === 0) {
        return (
            <div className="buyer-cart-content">
                <div className="buyer-cart-header">
                    <h1 className="buyer-cart-title">My Cart</h1>
                </div>
                <div className="buyer-empty-cart">
                    <i className="fa-solid fa-shopping-cart buyer-empty-cart-icon"></i>
                    <h3>Your cart is empty</h3>
                    <p>Add some items to get started!</p>
                    <button className="buyer-continue-shopping-btn" onClick={() => window.history.back()}>Continue Shopping</button>
                </div>
            </div>
        );
    }

    return (
        <div className="buyer-cart-content" style={{ padding: '20px' }}>
            <div className="buyer-cart-header" style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '20px' }}>
                <h1 className="buyer-cart-title">My Cart</h1>
                <button className="buyer-clear-cart-btn" onClick={handleClearCart}>
                    <i className="fa-solid fa-trash"></i> Clear Cart
                </button>
            </div>
            <div className="buyer-cart-items-section">
                {availableItems.length > 0 && (
                    <>
                        <h3 className="buyer-section-title">Available Items ({availableItems.length})</h3>
                        {availableItems.map(item => (
                            <CartItem
                                key={item._id}
                                item={item}
                                onQuantityChange={handleQuantityChange}
                                onRemove={handleRemoveItem}
                                onPlaceOrder={handlePlaceOrder}
                            />
                        ))}
                    </>
                )}
                {outOfStockItems.length > 0 && (
                    <>
                        <h3 className="buyer-section-title">Out of Stock Items ({outOfStockItems.length})</h3>
                        {outOfStockItems.map(item => (
                            <CartItem
                                key={item._id}
                                item={item}
                                onQuantityChange={handleQuantityChange}
                                onRemove={handleRemoveItem}
                                onPlaceOrder={handlePlaceOrder}
                            />
                        ))}
                    </>
                )}
            </div>
        </div>
    );
}

export default MyCartContent;
