import React, { useEffect, useState } from "react";
import "./Order.css";
import "./seller.css";

const getCurrentUserId = () => {
  const user = localStorage.getItem("user");
  if (user) {
    const userData = JSON.parse(user);
    return userData.id || userData._id || userData.userId;
  }
  return localStorage.getItem("userId");
};

const statusColors = {
  pending: "#f44336", // red
  confirmed: "#4caf50", // green
  delivered: "#ffc107", // amber
};

function Order() {
  const [orders, setOrders] = useState([]);
  const [notification, setNotification] = useState({
    show: false,
    message: "",
    isSuccess: true,
  });
  const [loading, setLoading] = useState({ type: "", id: null });

  const sellerId = getCurrentUserId();

  const showNotification = (message, isSuccess = true) => {
    setNotification({ show: true, message, isSuccess });
    setTimeout(() => {
      setNotification((prev) => ({ ...prev, show: false }));
    }, 3000);
  };

  const fetchOrders = async () => {
    try {
      const token = localStorage.getItem("token");
      const response = await fetch(`http://localhost:5000/api/orders/seller`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      const json = await response.json();

      if (!response.ok) throw new Error(json.message || "Failed to fetch orders");

      setOrders(json.data);
    } catch (error) {
      showNotification(error.message, false);
    }
  };

  useEffect(() => {
    if (sellerId) {
      fetchOrders();
    }
  }, [sellerId]);

  const updateOrderStatus = async (orderId, newStatus) => {
    setLoading({ type: "order", id: orderId });
    try {
      const token = localStorage.getItem("token");
      const response = await fetch(`http://localhost:5000/api/orders/${orderId}`, {
        method: "PATCH",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ status: newStatus }),
      });
      if (!response.ok) {
        const errData = await response.json();
        throw new Error(errData.message || "Failed to update order status");
      }

      await fetchOrders();

      let message = "";
      if (newStatus === "confirmed") {
        message = `Order #${orderId} accepted and being prepared!`;
      } else if (newStatus === "delivered") {
        message = `Order #${orderId} marked as delivered!`;
      }
      showNotification(message);
    } catch (error) {
      showNotification(error.message, false);
    } finally {
      setLoading({ type: "", id: null });
    }
  };

  const completeOrder = (orderId) => {
    const order = orders.find((o) => o._id === orderId);
    if (!order) return;

    if (order.status === "confirmed") {
      updateOrderStatus(orderId, "delivered");
    } else {
      showNotification("Cannot complete order unless it is confirmed.", false);
    }
  };

  // Group orders by status
  const groupedOrders = {
    pending: orders.filter((o) => o.status === "pending"),
    confirmed: orders.filter((o) => o.status === "confirmed"),
    delivered: orders.filter((o) => o.status === "delivered"),
  };

  // Helper to render orders grid by status
  const renderOrderSection = (status) => {
    const ordersOfStatus = groupedOrders[status];
    if (!ordersOfStatus.length) {
      return <p className="no-orders">No {status} orders.</p>;
    }
    return (
      <div className="orders-grid">
        {ordersOfStatus.map((order) => (
          <div key={order._id} className="order-card">
            <h3>
              Order #{order.orderNumber}
              <span
                className="order-status"
                style={{
                  backgroundColor: statusColors[order.status] || "grey",
                  color: "white",
                  padding: "0.25em 0.8em",
                  borderRadius: "20px",
                  marginLeft: "10px",
                  fontSize: "0.75em",
                  fontWeight: "600",
                  minWidth: "60px",
                  textAlign: "center",
                  display: "inline-block",
                }}
              >
                {order.status.charAt(0).toUpperCase() + order.status.slice(1)}
              </span>
            </h3>
            <div className="order-info">
              <p>
                <strong>Customer:</strong> {order.buyer.name || order.buyer.email}
              </p>
              <p>
                <strong>Address:</strong> {order.deliveryAddress}
              </p>
              <p>
                <strong>Mobile:</strong> {order.phoneNumber}
              </p>
              <p>
                <strong>Items:</strong>{" "}
                {order.items.map((item) => `${item.itemName} x${item.quantity}`).join(", ")}
              </p>
              <p>
                <strong>Total:</strong> Rs. {order.totalAmount}
              </p>
              <div className="action-buttons" style={{ display: "flex", gap: "10px" }}>
                {order.status === "pending" && (
                  <button
                    className="btn"
                    onClick={() => updateOrderStatus(order._id, "confirmed")}
                    disabled={loading.type === "order" && loading.id === order._id}
                  >
                    {loading.type === "order" && loading.id === order._id ? (
                      <>
                        <div className="loading-spinner"></div> Updating...
                      </>
                    ) : (
                      "Accept Order"
                    )}
                  </button>
                )}

                {(order.status === "confirmed" || order.status === "pending") && (
                  <button
                    className="btn btn-outline"
                    onClick={() => completeOrder(order._id)}
                    disabled={loading.type === "order" && loading.id === order._id}
                  >
                    {loading.type === "order" && loading.id === order._id ? (
                      <>
                        <div className="loading-spinner"></div> Updating...
                      </>
                    ) : (
                      "Complete Order"
                    )}
                  </button>
                )}

                {order.status === "delivered" && (
                  <button className="btn btn-outline" disabled>
                    Completed
                  </button>
                )}
              </div>
            </div>
          </div>
        ))}
      </div>
    );
  };

  return (
    <div id="orders" style={{ padding: "1rem 2rem", maxWidth: "1200px", margin: "auto" }}>
      <div className="header" style={{ marginBottom: "2rem" }}>
        <h1 style={{ fontSize: "2.5rem", marginBottom: "0.3rem" }}>Your Orders</h1>
        <p style={{ fontSize: "1.1rem", color: "#555" }}>
          Manage and track all your customer orders here.
        </p>
      </div>

      <section>
        <h2 className="section-title" style={{ borderBottom: "2px solid #4caf50", paddingBottom: "0.3rem" }}>
          Pending Orders
        </h2>
        {renderOrderSection("pending")}
      </section>

      <section style={{ marginTop: "3rem" }}>
        <h2 className="section-title" style={{ borderBottom: "2px solid #2196f3", paddingBottom: "0.3rem" }}>
          Confirmed Orders
        </h2>
        {renderOrderSection("confirmed")}
      </section>

      <section style={{ marginTop: "3rem" }}>
        <h2 className="section-title" style={{ borderBottom: "2px solid #ffc107", paddingBottom: "0.3rem" }}>
          Delivered Orders
        </h2>
        {renderOrderSection("delivered")}
      </section>

      {notification.show && (
        <div
          className={`notification ${notification.isSuccess ? "success" : "error"}`}
          style={{
            position: "fixed",
            bottom: "20px",
            right: "20px",
            padding: "1rem 1.5rem",
            borderRadius: "6px",
            color: "white",
            backgroundColor: notification.isSuccess ? "#4caf50" : "#f44336",
            boxShadow: "0 2px 8px rgba(0,0,0,0.2)",
            zIndex: 1000,
          }}
        >
          {notification.message}
        </div>
      )}
    </div>
  );
}

export default Order;
