import React, { useState, useEffect } from "react";
import "./seller-dasbboard.css";
import NotificationsComponent from "./Seller-Dashboard-Components/Notifications";
import Reviews from "./Seller-Dashboard-Components/Reviews";
import Messages from "./Seller-Dashboard-Components/Messages";
import Settings from "./Seller-Dashboard-Components/Settings";
import Menu from "./Seller-Dashboard-Components/Menu";
import Order from "./Seller-Dashboard-Components/Order";
import authService from "../../services/authService";

// Map status to colors - reuse from Order page
const statusColors = {
  pending: "red",
  confirmed: "#4caf50",
  delivered: "#ffc107",
};

const SellerDashboard = () => {
  const [activeSection, setActiveSection] = useState("dashboard");
  const [notification, setNotification] = useState({ show: false, message: "", isSuccess: true });
  const [userName, setUserName] = useState("");
  const [loading, setLoading] = useState({ type: "", id: null });
  const [userLoading, setUserLoading] = useState(true);

  // New state: pending orders list
  const [pendingOrders, setPendingOrders] = useState([]);

  // Helper: get token
  const getToken = () => localStorage.getItem("token");

  // Fetch user name
  useEffect(() => {
    const fetchUserName = async () => {
      try {
        setUserLoading(true);
        const currentUser = await authService.getCurrentUser();
        if (currentUser && currentUser.name) {
          setUserName(currentUser.name);
        }
      } catch (error) {
        console.error("Error fetching user name:", error);
        const storedUser = JSON.parse(localStorage.getItem("user") || "{}");
        if (storedUser && storedUser.name) {
          setUserName(storedUser.name);
        }
      } finally {
        setUserLoading(false);
      }
    };
    fetchUserName();
  }, []);

  // Fetch pending orders for the dashboard on load
  useEffect(() => {
    const fetchPendingOrders = async () => {
      try {
        const token = getToken();
        const response = await fetch("http://localhost:5000/api/orders/seller?status=pending", {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        const json = await response.json();
        if (!response.ok) throw new Error(json.message || "Failed to fetch pending orders");

        setPendingOrders(json.data || []);
      } catch (error) {
        showNotification(error.message, false);
      }
    };

    fetchPendingOrders();
  }, []);

  // Notification helper
  const showNotification = (message, isSuccess = true) => {
    setNotification({ show: true, message, isSuccess });
    setTimeout(() => setNotification((prev) => ({ ...prev, show: false })), 3000);
  };

  // Update order status (PATCH with token)
  const updateOrderStatus = async (orderId, newStatus) => {
    setLoading({ type: "order", id: orderId });
    try {
      const token = getToken();
      const response = await fetch(`http://localhost:5000/api/orders/${orderId}`, {
        method: "PATCH",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ status: newStatus }),
      });
      if (!response.ok) {
        const errData = await response.json();
        throw new Error(errData.message || "Failed to update order status");
      }

      // Refresh pending orders after update
      const refreshed = await fetch(`http://localhost:5000/api/orders/seller?status=pending`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      const refreshedJson = await refreshed.json();
      if (!refreshed.ok) throw new Error(refreshedJson.message || "Failed to refresh orders");
      setPendingOrders(refreshedJson.data || []);

      let message = "";
      if (newStatus === "confirmed") {
        message = `Order #${orderId} accepted and being prepared!`;
      } else if (newStatus === "delivered") {
        message = `Order #${orderId} marked as delivered!`;
      }
      showNotification(message);
    } catch (error) {
      showNotification(error.message, false);
    } finally {
      setLoading({ type: "", id: null });
    }
  };

  // Complete order handler
  const completeOrder = (orderId) => {
    const order = pendingOrders.find((o) => o._id === orderId);
    if (!order) return;

    if (order.status === "confirmed") {
      updateOrderStatus(orderId, "delivered");
    } else {
      showNotification("Cannot complete order unless it is confirmed.", false);
    }
  };

  // Render order card - same style as Order component
  const renderOrderCard = (order) => (
    <div key={order._id} className="order-card">
      <h3>
        Order #{order.orderNumber}
        <span
          className="order-status"
          style={{
            backgroundColor: statusColors[order.status] || "grey",
            color: "white",
            padding: "0.25em 0.8em",
            borderRadius: "20px",
            marginLeft: "10px",
            fontSize: "0.75em",
            fontWeight: "600",
            minWidth: "60px",
            textAlign: "center",
            display: "inline-block",
          }}
        >
          {order.status.charAt(0).toUpperCase() + order.status.slice(1)}
        </span>
      </h3>
      <div className="order-info">
        <p>
          <strong>Customer:</strong> {order.buyer.name || order.buyer.email}
        </p>
        <p>
          <strong>Address:</strong> {order.deliveryAddress}
        </p>
        <p>
          <strong>Mobile:</strong> {order.phoneNumber}
        </p>
        <p>
          <strong>Items:</strong>{" "}
          {order.items.map((item) => `${item.itemName} x${item.quantity}`).join(", ")}
        </p>
        <p>
          <strong>Total:</strong> Rs. {order.totalAmount}
        </p>
        <div className="action-buttons" style={{ display: "flex", gap: "10px" }}>
          {order.status === "pending" && (
            <button
              className="btn"
              onClick={() => updateOrderStatus(order._id, "confirmed")}
              disabled={loading.type === "order" && loading.id === order._id}
            >
              {loading.type === "order" && loading.id === order._id ? (
                <>
                  <div className="loading-spinner"></div> Updating...
                </>
              ) : (
                "Accept Order"
              )}
            </button>
          )}

          {(order.status === "confirmed" || order.status === "pending") && (
            <button
              className="btn btn-outline"
              onClick={() => completeOrder(order._id)}
              disabled={loading.type === "order" && loading.id === order._id}
            >
              {loading.type === "order" && loading.id === order._id ? (
                <>
                  <div className="loading-spinner"></div> Updating...
                </>
              ) : (
                "Complete Order"
              )}
            </button>
          )}

          {order.status === "delivered" && (
            <button className="btn btn-outline" disabled>
              Completed
            </button>
          )}
        </div>
      </div>
    </div>
  );

  const navItems = [
    { id: "dashboard", icon: "fas fa-home", label: "Dashboard" },
    { id: "orders", icon: "fas fa-shopping-bag", label: "Orders" },
    { id: "updateMenu", icon: "fas fa-utensils", label: "Menu Items" },
    { id: "review", icon: "fas fa-star", label: "Reviews" },
    { id: "messages", icon: "fas fa-comments", label: "Messages" },
    { id: "notifications", icon: "fas fa-bell", label: "Notifications" },
    { id: "settings", icon: "fas fa-cog", label: "Settings" },
  ];

  const showSection = (section) => setActiveSection(section);

  const renderDashboard = () => (
    <div id="dashboard">
      <div className="header">
        <h1>Welcome, {userLoading ? "Loading..." : userName || "Seller"}!</h1>
        <p>Here's an overview of your business performance</p>
      </div>
      <div className="stats-container">
        <div className="stat-card">
          <h3>Total Orders</h3>
          <div className="value">245</div>
        </div>
        <div className="stat-card">
          <h3>Today's Sales</h3>
          <div className="value">Rs. 12,450</div>
        </div>
        <div className="stat-card">
          <h3>New Customers</h3>
          <div className="value">8</div>
        </div>
        <div className="stat-card">
          <h3>Rating</h3>
          <div className="value">
            4.8 <i className="fas fa-star" style={{ color: "#FFC107", fontSize: "20px" }}></i>
          </div>
        </div>
      </div>
      {/* <div className="header">
        <h1>Recent Orders</h1>
        <p>Your latest orders requiring attention</p>
      </div>
      <div className="orders">
     
      </div> */}

      {/* NEW PENDING ORDERS SECTION */}
      <div className="header" style={{ marginTop: "2rem" }}>
        <h1>Pending Orders</h1>
        <p>Orders awaiting your confirmation</p>
      </div>
      <div className="orders">
        {pendingOrders.length === 0 && <p>No pending orders.</p>}
        {pendingOrders.map(renderOrderCard)}
      </div>
    </div>
  );

  const renderSection = {
    dashboard: renderDashboard,
    orders: () => <Order />,
    updateMenu: () => <Menu />,
    review: () => <Reviews />,
    messages: () => <Messages />,
    notifications: () => <NotificationsComponent />,
    settings: () => <Settings />,
  };

  return (
    <div style={{ display: "flex", minHeight: "100vh" }}>
      <div className="sidebar">
        <div className=".logo-container">
          <div className="logo">
            <i className="fas fa-store"></i>
            <span> LocalMart</span>
          </div>
        </div>
        {navItems.map((item) => (
          <a
            key={item.id}
            href="#"
            className={`nav-link ${activeSection === item.id ? "active" : ""}`}
            onClick={(e) => {
              e.preventDefault();
              showSection(item.id);
            }}
          >
            <i className={item.icon}></i>
            <span>{item.label}</span>
          </a>
        ))}
        <a
          href="#"
          className="nav-link"
          onClick={(e) => {
            e.preventDefault();
            if (window.confirm("Are you sure you want to logout?")) {
              authService.logout();
            }
          }}
        >
          <i className="fas fa-sign-out-alt"></i>
          <span>Logout</span>
        </a>
      </div>
      <div className="main">{renderSection[activeSection]()}</div>
      <div
        className={`notification ${notification.show ? "show" : ""}`}
        style={{ backgroundColor: notification.isSuccess ? "var(--success)" : "var(--danger)" }}
      >
        <span>{notification.message}</span>
      </div>
    </div>
  );
};

export default SellerDashboard;
