import React, { useEffect, useState } from "react";
import "./Order.css";
import "./seller.css";
import authService from '../../../services/authService';

function SellerDashboard() {
  const [sellerId, setSellerId] = useState(null);

  useEffect(() => {
    const user = authService.getCurrentUser(); // or however your authService provides user
    if (user) {
      setSellerId(user.id); // or user.id depending on your user object structure
    }
  }, []);

  if (!sellerId) {
    return <p>Loading seller info...</p>;
  }

  return <Order sellerId={sellerId} />;
}


function Order({ sellerId }) {
  console.log(sellerId);
  const [orders, setOrders] = useState([]);
  const [notification, setNotification] = useState({
    show: false,
    message: "",
    isSuccess: true,
  });
  const [loading, setLoading] = useState({ type: "", id: null });

  // Show notification helper
  const showNotification = (message, isSuccess = true) => {
    setNotification({ show: true, message, isSuccess });
    setTimeout(() => {
      setNotification((prev) => ({ ...prev, show: false }));
    }, 3000);
  };

  // Fetch orders for the seller
  const fetchOrders = async () => {
    try {
      const response = await fetch(`/api/orders?seller=${sellerId}`);
      if (!response.ok) throw new Error("Failed to fetch orders");
      const data = await response.json();
      setOrders(data);
    } catch (error) {
      showNotification(error.message, false);
    }
  };

  useEffect(() => {
    if (sellerId) {
      fetchOrders();
    }
  }, [sellerId]);

  // Update order status API call
  const updateOrderStatus = async (orderId, newStatus) => {
    setLoading({ type: "order", id: orderId });
    try {
      const response = await fetch(`/api/orders/${orderId}`, {
        method: "PATCH",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ status: newStatus }),
      });
      if (!response.ok) throw new Error("Failed to update order status");

      // Optionally get updated order from response if API returns it
      // const updatedOrder = await response.json();

      // Refresh orders list after update
      await fetchOrders();

      let message = "";
      if (newStatus === "confirmed") {
        message = `Order #${orderId} accepted and being prepared!`;
      } else if (newStatus === "delivered") {
        message = `Order #${orderId} marked as delivered!`;
      }
      showNotification(message);
    } catch (error) {
      showNotification(error.message, false);
    } finally {
      setLoading({ type: "", id: null });
    }
  };

  const viewOrderDetails = (orderId) => {
    showNotification(`Viewing details for Order #${orderId}`);
  };

  return (
    <div id="orders">
      <div className="header">
        <h1>All Orders</h1>
        <p>Manage and track all your customer orders here.</p>
      </div>

      <div className="orders">
        {orders.length === 0 && <p>No orders found.</p>}
        {orders.map((order) => (
          <div key={order._id} className="order-card">
            <h3>
              Order #{order.orderNumber}
              <span className="order-status">{order.status}</span>
            </h3>
            <div className="order-info">
              <p>
                <strong>Customer:</strong> {order.buyer.name || order.buyer.email}
              </p>
              <p>
                <strong>Address:</strong> {order.deliveryAddress}
              </p>
              <p>
                <strong>Mobile:</strong> {order.phoneNumber}
              </p>
              <p>
                <strong>Items:</strong>{" "}
                {order.items
                  .map((item) => `${item.itemName} x${item.quantity}`)
                  .join(", ")}
              </p>
              <p>
                <strong>Total:</strong> Rs. {order.totalAmount}
              </p>
              <div className="action-buttons">
                {order.status === "pending" && (
                  <button
                    className="btn"
                    onClick={() => updateOrderStatus(order._id, "confirmed")}
                    disabled={loading.type === "order" && loading.id === order._id}
                  >
                    {loading.type === "order" && loading.id === order._id ? (
                      <>
                        <div className="loading-spinner"></div> Updating...
                      </>
                    ) : (
                      "Accept Order"
                    )}
                  </button>
                )}
                {order.status === "confirmed" && (
                  <button
                    className="btn"
                    onClick={() => updateOrderStatus(order._id, "delivered")}
                    disabled={loading.type === "order" && loading.id === order._id}
                  >
                    {loading.type === "order" && loading.id === order._id ? (
                      <>
                        <div className="loading-spinner"></div> Updating...
                      </>
                    ) : (
                      "Mark as Delivered"
                    )}
                  </button>
                )}
                <button
                  className="btn btn-outline"
                  onClick={() => viewOrderDetails(order._id)}
                >
                  View Details
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>

      {notification.show && (
        <div
          className={`notification ${
            notification.isSuccess ? "success" : "error"
          }`}
        >
          {notification.message}
        </div>
      )}
    </div>
  );
}

export default Order;
