import React from 'react';
import { Heart } from 'lucide-react';
import { useState } from 'react';
import '@fortawesome/fontawesome-free/css/all.min.css'; // Import Font Awesome

// Data for categories and sellers
const categories = [
    { name: 'Food', img: 'https://via.placeholder.com/64' },
    { name: 'Clothing', img: 'https://via.placeholder.com/64' },
    { name: 'Electronics', img: 'https://via.placeholder.com/64' },
    { name: 'Books', img: 'https://via.placeholder.com/64' },
    { name: 'Crafts', img: 'https://via.placeholder.com/64' },
    { name: 'Essentials', img: 'https://via.placeholder.com/64' },
];

const sellers = [
    {
        category: 'Food',
        name: 'Campus Bites',
        img: 'https://via.placeholder.com/150',
        rating: 4,
        reviews: 46,
        description: 'Homemade meals and snacks for students. Daily specials and customizable menu options.',
    },
    {
        category: 'Crafts',
        name: 'Artistic Creations',
        img: 'https://via.placeholder.com/150',
        rating: 4,
        reviews: 28,
        description: 'Handmade crafts, calligraphy, and art pieces. Custom orders available.',
    },
    {
        category: 'Essentials',
        name: 'Hostel Supplies',
        img: 'https://via.placeholder.com/150',
        rating: 5,
        reviews: 52,
        description: 'All essential items for hostel life. Stationery, toiletries, and daily necessities.',
    },
];

// Sidebar Component
function Sidebar() {
    const menuItems = [
        { name: 'Dashboard', icon: 'fa-tachometer-alt' },
        { name: 'Explore Shops', icon: 'fa-store' },
        { name: 'My Cart', icon: 'fa-shopping-cart' },
        { name: 'My Orders', icon: 'fa-box' },
        { name: 'Favorites', icon: 'fa-heart' },
        { name: 'Reviews', icon: 'fa-star' },
        { name: 'Notifications', icon: 'fa-bell' },
        { name: 'Settings', icon: 'fa-cog' },
    ];

    return (
        <aside className="sidebar">
            <div className="sidebar-brand">
                <span className="navbar-brand">LocalMart</span>
            </div>
            <ul className="sidebar-menu">
                {menuItems.map((item, index) => (
                    <li key={index}>
                        <a href="#" className={`sidebar-link ${item.name === 'Dashboard' ? 'active' : ''}`}>
                            <i className={`fa-solid ${item.icon}`}></i>
                            <span>{item.name}</span>
                        </a>
                    </li>
                ))}
            </ul>
            <div className="sidebar-footer">
                <div className="user-info">
                    <i className="fa-solid fa-user-circle"></i>
                    <span>John Doe</span>
                </div>
                <a href="#" className="logout-button">
                    <i className="fa-solid fa-sign-out-alt"></i>
                    <span>Logout</span>
                </a>
            </div>
        </aside>
    );
}

// Category Card Component
function CategoryCard({ category }) {
    return (
        <div className="category-card">
            <img src={category.img} alt={category.name} className="category-image" />
            <span className="category-name">{category.name}</span>
        </div>
    );
}

// Seller Card Component
function SellerCard({ seller, heartSize = 32 }) {
    const [isFavorite, setIsFavorite] = useState(false);
    
    const handleFavoriteClick = (e) => {
        // Prevent the click from affecting parent elements
        e.stopPropagation();
        setIsFavorite(!isFavorite);
    };
    
    return (
        <div className="seller-card">
            <div className="seller-header">
                <span className="seller-category">{seller.category}</span>
                {/* Heart icon with toggle functionality */}
                <div 
                    onClick={handleFavoriteClick}
                    className="cursor-pointer flex items-center justify-center"
                >
                    <Heart 
                        className="favorite-icon transition-colors duration-200" 
                        fill={isFavorite ? "#f44336" : "none"} 
                        color={isFavorite ? "#f44336" : "currentColor"}
                        size={heartSize}
                    />
                </div>
            </div>
            <h3 className="seller-name">{seller.name}</h3>
            <div className="seller-rating">
                {[...Array(seller.rating)].map((_, i) => (
                    <i key={i} className="fa-solid fa-star star-icon"></i>
                ))}
                <span className="seller-reviews">({seller.reviews} reviews)</span>
            </div>
            <p className="seller-description">{seller.description}</p>
            <button className="seller-button">
                View {seller.category === 'Food' ? 'Menu' : 'Products'}
            </button>
        </div>
    );
}



// Special Offers Component
function SpecialOffers() {
    return (
        <div className="special-offers">
            <h2 className="offer-title">Special Offers:</h2>
            <p className="offer-description">20% off all food items this weekend!</p>
            <img src="https://via.placeholder.com/600x150" alt="Special Offer" className="offer-image" />
        </div>
    );
}

// Footer Component
function Footer() {
    return (
        <footer className="footer">
            <div className="footer-content">
                <span className="footer-logo">L</span>
                <span className="footer-text">LOCALMART - YOUR LOCAL MARKETPLACE</span>
            </div>
        </footer>
    );
}

// Dashboard Content Component
function DashboardContent() {
    return (
        <div className="dashboard-content">
            <div className="welcome-section">
                <div>
                    <h1 className="dashboard-title">Welcome back, John!</h1>
                    <p className="dashboard-subtitle">Here's what's happening with your orders today.</p>
                </div>
                <div className="action-buttons">
                    <button className="view-cart-button">View Cart</button>
                    <button className="new-order-button">+ New Order</button>
                </div>
            </div>
            <div className="stats-grid">
                <div className="stat-card total-orders">
                    <i className="fa-solid fa-shopping-bag stat-icon"></i>
                    <div>
                        <h3 className="stat-value">24</h3>
                        <p className="stat-label">Total Orders</p>
                    </div>
                </div>
                <div className="stat-card cart-items">
                    <i className="fa-solid fa-shopping-cart stat-icon"></i>
                    <div>
                        <h3 className="stat-value">5</h3>
                        <p className="stat-label">Cart Items</p>
                    </div>
                </div>
                <div className="stat-card favorite-sellers">
                    <i className="fa-solid fa-heart stat-icon"></i>
                    <div>
                        <h3 className="stat-value">12</h3>
                        <p className="stat-label">Favorite Sellers</p>
                    </div>
                </div>
                <div className="stat-card reviews-given">
                    <i className="fa-solid fa-star stat-icon"></i>
                    <div>
                        <h3 className="stat-value">18</h3>
                        <p className="stat-label">Reviews Given</p>
                    </div>
                </div>
            </div>
            <div className="category-section">
                <div className="section-header">
                    <h2 className="section-title">Shop by Category</h2>
                    <a href="#" className="view-all">View All</a>
                </div>
                <div className="category-grid">
                    {categories.map((category, index) => (
                        <CategoryCard key={index} category={category} />
                    ))}
                </div>
            </div>
            <div className="seller-section">
                <div className="section-header">
                    <h2 className="section-title">Featured Sellers</h2>
                    <a href="#" className="view-all">View All</a>
                </div>
                <div className="seller-grid">
                    {sellers.map((seller, index) => (
                        <SellerCard key={index} seller={seller} />
                    ))}
                </div>
            </div>
           
        </div>
    );
}

// Main BuyerDashboard Component
function BuyerDashboard() {
    return (
        <div className="app-container">
            <div className="main-container">
                <Sidebar />
                <DashboardContent />
            </div>
           
        </div>
    );
}

export default BuyerDashboard;
